import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { useTheme } from '../context/ThemeContext'
import { useAuth } from '../context/AuthContext'
import { collection, addDoc, doc, setDoc } from 'firebase/firestore'
import { db } from '../config/firebase'
import { CreditService } from '../services/creditService'
import { UserService } from '../services/userService'

export default function CreditApplication() {
  const [amount, setAmount] = useState('')
  const [term, setTerm] = useState('')
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState('')
  const [decisionResult, setDecisionResult] = useState<any>(null)
  const [isAnalyzing, setIsAnalyzing] = useState(false)
  const [isWithdrawing, setIsWithdrawing] = useState(false)

  const { theme } = useTheme()
  const { currentUser, updateUserBalance } = useAuth()
  const navigate = useNavigate()

  const { monthlyPayment, totalPayment, totalInterest } = CreditService.calculatePayments(amount, term)


  const analyzeWithDecisionEngine = async () => {
    const validation = CreditService.validateCreditApplication(amount, term)
    if (!validation.isValid) {
      setError(validation.error!)
      return
    }

    if (!currentUser) {
      setError('Kullanƒ±cƒ± giri≈üi gerekli')
      return
    }

    setIsAnalyzing(true)
    try {
      const loanAmount = parseFloat(amount)
      const loanTerm = parseInt(term)


      const applicationData = {
        loan_amount: parseFloat(amount),
        loan_term_months: parseInt(term),

        monthly_income: 22000,
        additional_income: 3000,
        expenses: 12000,
        rent_payment: 2500,
        age: 34,
        employment_type: '√ñzel Sekt√∂r',
        work_experience: 6,
        debt_to_income_ratio: 0.20,
        existing_loans: 25000,
        credit_card_limit: 30000,
        credit_card_debt: 4000,
        bank_balance: 35000,
        investments: 75000,
        real_estate_value: 450000,
        kkb_score: 720,
        payment_delays: 0,
        home_ownership: 'owner',
        residence_duration: 48,
        customer_segment: 'mass',
        existing_relationship: 24,
        total_banking_products: 3,

        defaulted_loans: false,
        legal_issues: false,
        has_insurance: true,
        job_stability: 'stable'
      }


      try {
        const userProfileData = {
          ...applicationData,
          userId: currentUser.uid,
          userEmail: currentUser.email,
          timestamp: new Date(),
          evaluation_type: 'user_credit_profile',
          system_version: 'v2.0_comprehensive_data'
        }

        const docRef = await addDoc(collection(db, 'user_profiles'), userProfileData)
      } catch (firebaseError) {
        console.warn('Firestore\'a veri kaydedilirken hata:', firebaseError)
      }


      const response = await fetch(`https://us-central1-finisbank.cloudfunctions.net/evaluate_credit`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(applicationData)
      })
      if (response.ok) {
        const result = await response.json()

        const processedResult = {
          ...result,
          approved: result.decision === "ONAYLANDI"
        }

        setDecisionResult(processedResult)
      } else {
        try {
          const errorText = await response.text()
          console.error('API yanƒ±t hatasƒ±:', errorText)
          setError('Kredi deƒüerlendirme servisi ge√ßici olarak kullanƒ±lamƒ±yor.')
        } catch (textError) {
          console.error('API hata mesajƒ± okunamadƒ±:', textError)
          setError('Kredi deƒüerlendirme sƒ±rasƒ±nda beklenmedik hata olu≈ütu.')
        }
      }
    } catch (error) {
      console.error('Kredi analiz hatasƒ±:', error)
      setError('Kredi analizi sƒ±rasƒ±nda hata olu≈ütu. L√ºtfen tekrar deneyiniz.')
    } finally {
      setIsAnalyzing(false)
    }
  }

  const withdrawCreditToAccount = async () => {
    if (!decisionResult || !decisionResult.approved || !currentUser) return

    setIsWithdrawing(true)
    try {
      const creditAmount = decisionResult.recommended_amount || parseFloat(amount)

      await updateUserBalance(creditAmount)

      const creditRecord = {
        userId: currentUser.uid,
        userEmail: currentUser.email,
        amount: creditAmount,
        term: parseInt(term),
        monthlyPayment: monthlyPayment,
        totalPayment: totalPayment,
        withdrawnAt: new Date(),
        status: 'Aktif',
        creditType: 'AI Onaylƒ± Kredi'
      }

      await addDoc(collection(db, 'activeCredits'), creditRecord)

      alert(`Kredi tutarƒ± (${creditAmount.toLocaleString('tr-TR')} TL) hesabƒ±nƒ±za ba≈üarƒ±yla eklendi!`)
      navigate('/account')

    } catch (error: any) {
      alert('Kredi hesabƒ±nƒ±za eklenirken hata olu≈ütu: ' + (error?.message || 'Bilinmeyen hata'))
    } finally {
      setIsWithdrawing(false)
    }
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()

    if (!currentUser) {
      setError('Kredi ba≈üvurusu i√ßin giri≈ü yapmalƒ±sƒ±nƒ±z.')
      return
    }

    if (!amount || !term) {
      setError('L√ºtfen tutar ve vade alanlarƒ±nƒ± doldurun.')
      return
    }

    if (parseFloat(amount) < 1000) {
      setError('Minimum kredi tutarƒ± 1.000 TL olmalƒ±dƒ±r.')
      return
    }

    if (parseInt(term) < 3 || parseInt(term) > 240) {
      setError('Kredi vadesi 3-240 ay arasƒ±nda olmalƒ±dƒ±r.')
      return
    }

    try {
