import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { useTheme } from '../context/ThemeContext'
import { useAuth } from '../context/AuthContext'
import { FormService, PROFESSIONS } from '../services/formService'
import { UserService } from '../services/userService'

export default function Register() {
  // Basic Info
  const [name, setName] = useState('')
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [confirmPassword, setConfirmPassword] = useState('')
  const [age, setAge] = useState('')

  // Professional Info
  const [profession, setProfession] = useState('')
  const [experience, setExperience] = useState('')
  const [sector, setSector] = useState('')

  // Financial Info
  const [salary, setSalary] = useState('')
  const [additionalIncome, setAdditionalIncome] = useState('')

  const [error, setError] = useState('')
  const [loading, setLoading] = useState(false)
  const { theme } = useTheme()
  const { register } = useAuth()
  const navigate = useNavigate()


  const fillWithMockData = () => {
    if (!import.meta.env.DEV) return;

    const randomUser = FormService.getRandomMockUser()
    const randomPassword = FormService.getMockPassword()

    setName(randomUser.name)
    setEmail(randomUser.email)
    setAge(randomUser.age)
    setProfession(randomUser.profession)
    setExperience(randomUser.experience)
    setSector(randomUser.sector)
    setSalary(randomUser.salary)
    setAdditionalIncome(randomUser.additionalIncome)
    setPassword(randomPassword)
    setConfirmPassword(randomPassword)
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()

    const validation = FormService.validateRegistrationForm({
      password,
      confirmPassword,
      age,
      profession,
      sector
    })

    if (!validation.isValid) {
      setError(validation.error!)
      return
    }

    try {
      setError('')
      setLoading(true)

      const userData = UserService.prepareRegistrationData({
        age,
        profession,
        experience,
        sector,
        salary,
        additionalIncome
      })

      await register(name, email, password, userData)

      navigate('/')
    } catch (error: any) {
      setError('Kayƒ±t ba≈üarƒ±sƒ±z: ' + (error.message || 'Bilinmeyen hata'))
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className={`app-${theme}`} style={{ minHeight: '100vh', padding: '20px' }}>
      <div className="container" style={{ maxWidth: '600px', margin: '0 auto' }}>
      <div style={{ textAlign: 'center', marginBottom: '30px' }}>
        <h1>üè¶ Fini≈ü Bankasƒ±</h1>
        <h2>Yeni Hesap A√ßƒ±n</h2>
        {import.meta.env.DEV && (
          <button 
            type="button"
            onClick={fillWithMockData}
            style={{
              padding: '8px 16px',
              backgroundColor: '#17a2b8',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '14px',
              marginBottom: '15px'
            }}
          >
            üé≤ Test Verilerini Doldur
          </button>
        )}
        {error && (
          <div style={{ 
            color: '#dc3545', 
            backgroundColor: 'rgba(220, 53, 69, 0.1)', 
            padding: '10px', 
            borderRadius: '4px', 
            marginTop: '10px',
            fontSize: '14px'
          }}>
            {error}
          </div>
        )}
      </div>
      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>

        <div className={`card-${theme}`} style={{ padding: '20px', borderRadius: '8px' }}>
          <h3 style={{ marginTop: 0, marginBottom: '15px', color: '#007bff' }}>Ki≈üisel Bilgiler</h3>

          <div className="form-grid-2">
            <div>
              <label htmlFor="name">Ad Soyad: *</label>
              <input
                type="text"
                id="name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
                className={`input-${theme}`}
                style={{ width: '100%', padding: '8px', marginTop: '5px' }}
              />
            </div>
            <div>
              <label htmlFor="age">Ya≈ü: *</label>
              <input
                type="number"
                id="age"
                value={age}
                onChange={(e) => setAge(e.target.value)}
                required
                min="18"
                max="100"
                className={`input-${theme}`}
                style={{ width: '100%', padding: '8px', marginTop: '5px' }}
              />
            </div>
          </div>

          <div style={{ marginTop: '15px' }}>
            <label htmlFor="email">E-posta: *</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              className={`input-${theme}`}
              style={{ width: '100%', padding: '8px', marginTop: '5px' }}
            />
          </div>
        </div>

        <div className={`card-${theme}`} style={{ padding: '20px', borderRadius: '8px' }}>
          <h3 style={{ marginTop: 0, marginBottom: '15px', color: '#007bff' }}>üíº Meslek Bilgileri</h3>

          <div className="form-grid-2">
            <div>
              <label htmlFor="profession">Meslek: *</label>
              <select
                id="profession"
                value={profession}
                onChange={(e) => setProfession(e.target.value)}
                required
                className={`input-${theme}`}
                style={{ width: '100%', padding: '8px', marginTop: '5px' }}
              >
                <option value="">Meslek Se√ßiniz</option>
                {PROFESSIONS.map((prof) => (
                  <option key={prof} value={prof}>{prof}</option>
                ))}
              </select>
            </div>
            <div>
              <label htmlFor="experience">Tecr√ºbe (Yƒ±l):</label>
              <input
                type="number"
                id="experience"
                value={experience}
                onChange={(e) => setExperience(e.target.value)}
                min="0"
                max="50"
                className={`input-${theme}`}
                style={{ width: '100%', padding: '8px', marginTop: '5px' }}
                placeholder="0"
              />
            </div>
