// Frontend data validation utilities
import { CONSTANTS } from './constants'

export interface ValidationResult {
  isValid: boolean
  errors: string[]
  sanitizedData?: any
}

export class DataValidator {

  static validateCreditApplication(data: any): ValidationResult {
    const errors: string[] = []
    const sanitizedData: any = {}

    // Amount validation
    const amount = parseFloat(data.amount)
    if (isNaN(amount)) {
      errors.push('Kredi tutarƒ± ge√ßerli bir sayƒ± olmalƒ±dƒ±r')
    } else if (amount < CONSTANTS.CREDIT.MIN_AMOUNT) {
      errors.push(`Minimum kredi tutarƒ± ${CONSTANTS.CREDIT.MIN_AMOUNT.toLocaleString('tr-TR')} TL olmalƒ±dƒ±r`)
    } else if (amount > CONSTANTS.CREDIT.MAX_AMOUNT) {
      errors.push(`Maksimum kredi tutarƒ± ${CONSTANTS.CREDIT.MAX_AMOUNT.toLocaleString('tr-TR')} TL olmalƒ±dƒ±r`)
    } else {
      sanitizedData.amount = amount
    }

    // Term validation
    const term = parseInt(data.term)
    if (isNaN(term)) {
      errors.push('Vade ge√ßerli bir sayƒ± olmalƒ±dƒ±r')
    } else if (term < CONSTANTS.CREDIT.MIN_TERM) {
      errors.push(`Minimum vade ${CONSTANTS.CREDIT.MIN_TERM} ay olmalƒ±dƒ±r`)
    } else if (term > CONSTANTS.CREDIT.MAX_TERM) {
      errors.push(`Maksimum vade ${CONSTANTS.CREDIT.MAX_TERM} ay olmalƒ±dƒ±r`)
    } else {
      sanitizedData.term = term
    }

    return {
      isValid: errors.length === 0,
      errors,
      sanitizedData: errors.length === 0 ? sanitizedData : undefined
    }
  }

  static validateUserRegistration(data: any): ValidationResult {
    const errors: string[] = []
    const sanitizedData: any = {}

    // Name validation
    const name = data.name?.trim()
    if (!name || name.length < 2) {
      errors.push('Ad en az 2 karakter olmalƒ±dƒ±r')
    } else if (name.length > 50) {
      errors.push('Ad en fazla 50 karakter olmalƒ±dƒ±r')
    } else if (!/^[a-zA-Zƒü√º≈ü√∂√ßƒ±ƒû√ú≈û√ñ√áƒ∞\s]+$/.test(name)) {
      errors.push('Ad sadece harf ve bo≈üluk i√ßerebilir')
    } else {
      sanitizedData.name = name
    }

    // Email validation
    const email = data.email?.toLowerCase().trim()
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/
    if (!email) {
      errors.push('E-posta adresi gereklidir')
    } else if (!emailRegex.test(email)) {
      errors.push('Ge√ßerli bir e-posta adresi giriniz')
    } else {
      sanitizedData.email = email
    }

    // Password validation
    if (data.password) {
      const passwordValidation = this.validatePassword(data.password)
      if (!passwordValidation.isValid) {
        errors.push(...passwordValidation.errors)
      }
    }

    // Phone number validation (optional)
    if (data.phoneNumber) {
      const phone = data.phoneNumber.replace(/\s+/g, '')
      const phoneRegex = /^(\+90|0)?[5][0-9]{9}$/
      if (!phoneRegex.test(phone)) {
        errors.push('Ge√ßerli bir telefon numarasƒ± giriniz (05XX XXX XX XX)')
      } else {
        sanitizedData.phoneNumber = phone
      }
    }

    return {
      isValid: errors.length === 0,
      errors,
      sanitizedData: errors.length === 0 ? sanitizedData : undefined
    }
  }

  static validatePassword(password: string): ValidationResult {
    const errors: string[] = []

    if (!password) {
      errors.push('≈ûifre gereklidir')
      return { isValid: false, errors }
    }

    if (password.length < 8) {
      errors.push('≈ûifre en az 8 karakter olmalƒ±dƒ±r')
    }

    if (!/(?=.*[a-z])/.test(password)) {
      errors.push('≈ûifre en az bir k√º√ß√ºk harf i√ßermelidir')
    }

    if (!/(?=.*[A-Z])/.test(password)) {
      errors.push('≈ûifre en az bir b√ºy√ºk harf i√ßermelidir')
    }

    if (!/(?=.*\d)/.test(password)) {
      errors.push('≈ûifre en az bir rakam i√ßermelidir')
    }

    if (!/(?=.*[!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?])/.test(password)) {
      errors.push('≈ûifre en az bir √∂zel karakter i√ßermelidir')
    }

    // Common passwords check
    const commonPasswords = [
      '12345678', 'password', 'qwerty123', 'admin123', 
      'password123', '123456789', 'welcome123'
    ]

    if (commonPasswords.includes(password.toLowerCase())) {
      errors.push('Bu ≈üifre √ßok yaygƒ±n kullanƒ±lmaktadƒ±r, daha g√ºvenli bir ≈üifre se√ßiniz')
    }

    return {
      isValid: errors.length === 0,
      errors
    }
  }

  static sanitizeHtml(input: string): string {
    // Basic HTML sanitization
    return input
      .replace(/&/g, '&amp;')
      .replace(/</g, '&lt;')
      .replace(/>/g, '&gt;')
      .replace(/"/g, '&quot;')
      .replace(/'/g, '&#x27;')
      .replace(/\//g, '&#x2F;')
  }

  static validateBankingData(data: any): ValidationResult {
    const errors: string[] = []
    const sanitizedData: any = {}

    // Account number validation (Turkish IBAN or account number format)
    if (data.accountNumber) {
      const accountNumber = data.accountNumber.replace(/\s+/g, '')
      if (!/^(TR\d{24}|\d{4}-\d{6})$/.test(accountNumber)) {
        errors.push('Ge√ßerli bir hesap numarasƒ± giriniz (IBAN veya XXXX-XXXXXX formatƒ±nda)')
      } else {
        sanitizedData.accountNumber = accountNumber
      }
    }

    // Balance validation
    if (data.balance !== undefined) {
      const balance = parseFloat(data.balance)
      if (isNaN(balance) || balance < 0) {
        errors.push('Bakiye sƒ±fƒ±r veya pozitif bir deƒüer olmalƒ±dƒ±r')
      } else {
        sanitizedData.balance = balance
      }
    }

    return {
      isValid: errors.length === 0,
      errors,
      sanitizedData: errors.length === 0 ? sanitizedData : undefined
    }
  }
}

