// Centralized error handling
import { CONSTANTS } from './constants'

export interface AppError {
  code: string
  message: string
  details?: any
  timestamp: Date
}

export class ErrorHandler {
  static createError(code: string, message: string, details?: any): AppError {
    return {
      code,
      message,
      details,
      timestamp: new Date()
    }
  }

  static handleFirebaseError(error: any): AppError {
    const { code, message } = error

    switch (code) {
      case 'auth/user-not-found':
        return this.createError('USER_NOT_FOUND', 'Kullanƒ±cƒ± bulunamadƒ±')
      case 'auth/wrong-password':
        return this.createError('WRONG_PASSWORD', '≈ûifre hatalƒ±')
      case 'auth/email-already-in-use':
        return this.createError('EMAIL_IN_USE', 'Bu e-posta adresi zaten kullanƒ±mda')
      case 'auth/weak-password':
        return this.createError('WEAK_PASSWORD', '≈ûifre √ßok zayƒ±f')
      case 'auth/invalid-email':
        return this.createError('INVALID_EMAIL', 'Ge√ßersiz e-posta adresi')
      case 'permission-denied':
        return this.createError('PERMISSION_DENIED', 'Bu i≈ülem i√ßin yetkiniz yok')
      case 'unavailable':
        return this.createError('SERVICE_UNAVAILABLE', 'Servis ≈üu anda kullanƒ±lamƒ±yor')
      default:
        return this.createError('UNKNOWN_ERROR', message || CONSTANTS.MESSAGES.ERROR.UNKNOWN)
    }
  }

  static handleNetworkError(error: any): AppError {
    if (!navigator.onLine) {
      return this.createError('OFFLINE', 'ƒ∞nternet baƒülantƒ±nƒ±zƒ± kontrol ediniz')
    }

    if (error.name === 'AbortError') {
      return this.createError('REQUEST_TIMEOUT', 'ƒ∞stek zaman a≈üƒ±mƒ±na uƒüradƒ±')
    }

    return this.createError('NETWORK_ERROR', CONSTANTS.MESSAGES.ERROR.NETWORK)
  }

  static logError(error: AppError, context?: string): void {
    console.error(`[${error.timestamp.toISOString()}] ${context || 'APP_ERROR'}:`, {
      code: error.code,
      message: error.message,
      details: error.details
    })
  }
}

