export const SECURITY_HEADERS = {
  // Content Security Policy
  CSP: {
    directives: [
      "default-src 'self'",
      "script-src 'self' 'unsafe-inline' https://www.gstatic.com https://www.googleapis.com",
      "style-src 'self' 'unsafe-inline' https://fonts.googleapis.com",
