// Input validation utilities
export const validators = {
  email: (email: string): boolean => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/
    return emailRegex.test(email)
  },

  password: (password: string): { isValid: boolean; message?: string } => {
    if (password.length < 6) {
      return { isValid: false, message: '≈ûifre en az 6 karakter olmalƒ±dƒ±r' }
    }
    if (!/(?=.*[a-z])(?=.*[A-Z])/.test(password)) {
      return { isValid: false, message: '≈ûifre b√ºy√ºk ve k√º√ß√ºk harf i√ßermelidir' }
    }
    if (!/(?=.*\d)/.test(password)) {
      return { isValid: false, message: '≈ûifre en az bir rakam i√ßermelidir' }
    }
    return { isValid: true }
  },

  phoneNumber: (phone: string): boolean => {
    const phoneRegex = /^(\+90|0)?[5][0-9]{9}$/
    return phoneRegex.test(phone.replace(/\s+/g, ''))
  },

  creditAmount: (amount: string): { isValid: boolean; message?: string } => {
    const numAmount = parseFloat(amount)
    if (isNaN(numAmount)) {
      return { isValid: false, message: 'Ge√ßerli bir tutar giriniz' }
    }
    if (numAmount < 1000) {
      return { isValid: false, message: 'Minimum kredi tutarƒ± 1.000 TL olmalƒ±dƒ±r' }
    }
    if (numAmount > 1000000) {
      return { isValid: false, message: 'Maksimum kredi tutarƒ± 1.000.000 TL olmalƒ±dƒ±r' }
    }
    return { isValid: true }
  },

  creditTerm: (term: string): { isValid: boolean; message?: string } => {
    const numTerm = parseInt(term)
    if (isNaN(numTerm)) {
      return { isValid: false, message: 'Ge√ßerli bir vade giriniz' }
    }
    if (numTerm < 3) {
      return { isValid: false, message: 'Minimum vade 3 ay olmalƒ±dƒ±r' }
    }
    if (numTerm > 240) {
      return { isValid: false, message: 'Maksimum vade 240 ay olmalƒ±dƒ±r' }
    }
    return { isValid: true }
  }
}

