import { useCallback } from 'react'
import { useAsync } from '../../../hooks/useAsync'
import { creditService } from '../services/creditService'
import { creditRepository } from '../services/creditRepository'
import { CreditApplication, DecisionResult, UserProfile } from '../types/credit'

export function useDecisionEngine() {
  const { execute, isLoading, error, data } = useAsync<DecisionResult>()

  const analyzeCredit = useCallback(
    async (applicationData: CreditApplication, userContext: { uid: string; email: string }) => {
      const analysisFunction = async () => {
        // Save user profile data
        try {
          const userProfileData: UserProfile = {
            ...applicationData,
            userId: userContext.uid,
            userEmail: userContext.email,
            timestamp: new Date(),
            evaluation_type: 'user_credit_profile',
            system_version: 'v2.0_comprehensive_data'
          }

          await creditRepository.saveCreditApplication(userProfileData)
        } catch (firebaseError) {
          console.warn('Firestore\'a veri kaydedilirken hata:', firebaseError)
        }

        // Evaluate credit through AI service
        const result = await creditService.evaluateCredit(applicationData)
        return result
      }

      return execute(analysisFunction)
    },
    [execute]
  )

  return {
    analyzeCredit,
    isAnalyzing: isLoading,
    error,
    decisionResult: data,
  }
}

