import { useCallback } from 'react'
import { useAsync } from '../../../hooks/useAsync'
import { creditRepository } from '../services/creditRepository'
import { CreditRecord, DecisionResult } from '../types/credit'

interface WithdrawCreditParams {
  amount: number
  term: number
  decisionResult: DecisionResult
  userId: string
  updateUserBalance: (newBalance: number) => Promise<void>
}

export function useWithdrawCredit() {
  const { execute, isLoading, error } = useAsync<void>()

  const withdrawCredit = useCallback(
    async ({ amount, term, decisionResult, userId, updateUserBalance }: WithdrawCreditParams) => {
      const withdrawFunction = async () => {
        if (!decisionResult.approved) {
          throw new Error('Onaylanmamƒ±≈ü kredi √ßekilemez')
        }

        const creditRecord: CreditRecord = {
          userId,
          amount,
          term,
          monthlyPayment: decisionResult.approval_details?.monthly_payment || 0,
          status: 'active',
          startDate: new Date(),
          nextPaymentDate: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000), // 30 days later
          remainingBalance: amount,
          decisionResult,
        }

        // Save credit record
        await creditRepository.saveActiveCredit(creditRecord)

        // Update user balance
        await updateUserBalance(amount)
      }

      return execute(withdrawFunction)
    },
    [execute]
  )

  return {
    withdrawCredit,
    isWithdrawing: isLoading,
    error,
  }
}

