import { firestoreClient } from '../../../shared/services/firestoreClient'
import { FIRESTORE_COLLECTIONS } from '../constants'
import { CreditRecord, UserProfile } from '../types/credit'

export class CreditRepository {
  async saveCreditApplication(applicationData: UserProfile): Promise<string> {
    try {
      return await firestoreClient.add(FIRESTORE_COLLECTIONS.USER_PROFILES, applicationData)
    } catch (error) {
      console.warn('Firestore\'a veri kaydedilirken hata:', error)
      throw error
    }
  }

  async saveActiveCredit(creditRecord: CreditRecord): Promise<string> {
    try {
      return await firestoreClient.add(FIRESTORE_COLLECTIONS.ACTIVE_CREDITS, creditRecord)
    } catch (error) {
      console.error('Active credit save failed:', error)
      throw error
    }
  }

  async getUserCredits(userId: string): Promise<CreditRecord[]> {
    try {
      return await firestoreClient.getWhere<CreditRecord>(
        FIRESTORE_COLLECTIONS.ACTIVE_CREDITS,
        'userId',
        '==',
        userId
