src/shared/services/firestoreClient.ts.content:
import { collection, addDoc, doc, setDoc, getDoc, getDocs, query, where, updateDoc, deleteDoc } from 'firebase/firestore'
import { db } from '../../config/firebase'

export class FirestoreClient {
  async add<T>(collectionName: string, data: T): Promise<string> {
    try {
      const docRef = await addDoc(collection(db, collectionName), data)
      return docRef.id
    } catch (error) {
      console.error(`Error adding document to ${collectionName}:`, error)
      throw error
    }
  }

  async set<T>(collectionName: string, documentId: string, data: T): Promise<void> {
    try {
      await setDoc(doc(db, collectionName, documentId), data)
    } catch (error) {
      console.error(`Error setting document in ${collectionName}:`, error)
      throw error
    }
  }

  async get<T>(collectionName: string, documentId: string): Promise<T | null> {
    try {
      const docRef = doc(db, collectionName, documentId)
      const docSnap = await getDoc(docRef)

      if (docSnap.exists()) {
        return { id: docSnap.id, ...docSnap.data() } as unknown as T
      } else {
        return null
      }
    } catch (error) {
      console.error(`Error getting document from ${collectionName}:`, error)
      throw error
    }
  }

  async getAll<T>(collectionName: string): Promise<T[]> {
    try {
      const querySnapshot = await getDocs(collection(db, collectionName))
      return querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }) as unknown as T)
    } catch (error) {
      console.error(`Error getting documents from ${collectionName}:`, error)
      throw error
    }
  }

  async getWhere<T>(collectionName: string, field: string, operator: any, value: any): Promise<T[]> {
    try {
      const q = query(collection(db, collectionName), where(field, operator, value))
      const querySnapshot = await getDocs(q)
      return querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }) as unknown as T)
    } catch (error) {
      console.error(`Error querying documents from ${collectionName}:`, error)
      throw error
    }
  }

  async update(collectionName: string, documentId: string, data: Partial<any>): Promise<void> {
    try {
      const docRef = doc(db, collectionName, documentId)
      await updateDoc(docRef, data)
    } catch (error) {
      console.error(`Error updating document in ${collectionName}:`, error)
      throw error
    }
  }

  async delete(collectionName: string, documentId: string): Promise<void> {
    try {
      await deleteDoc(doc(db, collectionName, documentId))
    } catch (error) {
      console.error(`Error deleting document from ${collectionName}:`, error)
      throw error
    }
  }
}

export const firestoreClient = new FirestoreClient()

src/shared/services/httpClient.ts.content:
class HttpClient {
  private baseURL: string

  constructor(baseURL: string = '') {
    this.baseURL = baseURL
  }

  async request<T>(url: string, options: RequestInit = {}): Promise<T> {
    const fullUrl = this.baseURL ? `${this.baseURL}${url}` : url

    const defaultHeaders = {
      'Content-Type': 'application/json',
    }

    const config = {
      ...options,
      headers: {
        ...defaultHeaders,
        ...options.headers,
      },
    }

    try {
      const response = await fetch(fullUrl, config)

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`)
      }

      return await response.json()
    } catch (error) {
      console.error('HTTP request failed:', error)
      throw error
    }
  }

  async get<T>(url: string, options?: RequestInit): Promise<T> {
    return this.request<T>(url, { ...options, method: 'GET' })
  }

  async post<T>(url: string, data?: any, options?: RequestInit): Promise<T> {
    return this.request<T>(url, {
      ...options,
      method: 'POST',
      body: data ? JSON.stringify(data) : undefined,
    })
  }

  async put<T>(url: string, data?: any, options?: RequestInit): Promise<T> {
    return this.request<T>(url, {
      ...options,
      method: 'PUT',
      body: data ? JSON.stringify(data) : undefined,
    })
  }

  async delete<T>(url: string, options?: RequestInit): Promise<T> {
    return this.request<T>(url, { ...options, method: 'DELETE' })
  }
}

export const httpClient = new HttpClient()
export default HttpClient

