interface NumberInputProps {
  value: string
  onChange: (value: string) => void
  placeholder?: string
  min?: number
  max?: number
  step?: number
  label?: string
  error?: string
  disabled?: boolean
}

export function NumberInput({
  value,
  onChange,
  placeholder,
  min,
  max,
  step,
  label,
  error,
  disabled = false
}: NumberInputProps) {
  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newValue = e.target.value

    // Allow empty value for clearing
    if (newValue === '') {
      onChange('')
      return
    }

    // Only allow numbers and decimal point
    if (!/^\d*\.?\d*$/.test(newValue)) {
      return
    }

    onChange(newValue)
  }

  return (
    <div className="number-input-container">
      {label && (
        <label className="number-input-label">
          {label}
        </label>
      )}
      <input
        type="text"
        value={value}
        onChange={handleChange}
        placeholder={placeholder}
        disabled={disabled}
        min={min}
        max={max}
        step={step}
        className={`number-input ${error ? 'error' : ''}`}
      />
      {error && (
        <div className="number-input-error">
          {error}
        </div>
      )}
    </div>
  )
}

src/hooks/useAsync.ts.content:
import { useState, useCallback } from 'react'
import type { AsyncState } from '../shared/types/common'

export function useAsync<T>() {
  const [state, setState] = useState<AsyncState<T>>({
    data: null,
    isLoading: false,
    error: null,
  })

  const execute = useCallback(
    async (asyncFunction: () => Promise<T>) => {
      setState(prev => ({ ...prev, isLoading: true, error: null }))

      try {
        const data = await asyncFunction()
        setState({ data, isLoading: false, error: null })
        return data
      } catch (error) {
        const errorMessage = error instanceof Error ? error.message : 'Bir hata olu≈ütu'
        setState({ data: null, isLoading: false, error: errorMessage })
        throw error
      }
    },
    []
  )

  const reset = useCallback(() => {
    setState({ data: null, isLoading: false, error: null })
  }, [])

  return {
    ...state,
    execute,
    reset
  }
}

