  margin-bottom: 15px;
}

.form-field label {
  display: block;
  margin-bottom: 5px;
  font-weight: 600;
  color: var(--text-light);
  font-size: 14px;
}

.form-field input,
.form-field select {
  width: 100%;
  padding: 8px;
  border: 2px solid rgba(255, 255, 255, 0.3);
  border-radius: var(--border-radius);
  background: #808080;
  color: var(--text-light);
  font-size: 16px;
  transition: var(--transition);
  backdrop-filter: blur(10px);
}

.form-field input::placeholder {
  color: rgba(255, 255, 255, 0.6);
}

.form-field input:focus,
.form-field select:focus {
  outline: none;
  border-color: rgba(255, 255, 255, 0.6);
  background: rgba(255, 255, 255, 0.2);
}

.register-submit-btn {
  padding: 12px 20px;
  border-radius: 4px;
  cursor: pointer;
  border: none;
  font-size: 16px;
  font-weight: 600;
  transition: var(--transition);
  width: 100%;
  background: linear-gradient(135deg, #28a745, #20c997);
  color: white;
}

.register-submit-btn:hover:not(:disabled) {
  background: linear-gradient(135deg, #218838, #1fa085);
  transform: translateY(-2px);
  box-shadow: var(--shadow);
}

.register-submit-btn:disabled {
  cursor: not-allowed;
  opacity: 0.7;
  background: #6c757d;
}

/* ========================================
   LOGIN FORM STYLES
   ======================================== */

.login-form {
  display: flex;
  flex-direction: column;
  gap: 15px;
}

.login-form-group {
  display: flex;
  flex-direction: column;
}

.login-form-label {
  margin-bottom: 5px;
  font-weight: 600;
  color: var(--text-light);
}

.login-form-input {
  width: 100%;
  padding: 8px;
  border: 2px solid rgba(255, 255, 255, 0.3);
  border-radius: 4px;
  background: #808080;
  color: var(--text-light);
  font-size: 16px;
  transition: var(--transition);
  backdrop-filter: blur(10px);
}

.login-form-input::placeholder {
  color: rgba(255, 255, 255, 0.6);
}

.login-form-input:focus {
  outline: none;
  border-color: rgba(255, 255, 255, 0.6);
  background: rgba(255, 255, 255, 0.2);
}

.login-submit-button {
  padding: 10px;
  border-radius: 4px;
  cursor: pointer;
  background: rgba(255, 255, 255, 0.2);
  color: var(--text-light);
  border: none;
  font-size: 16px;
  font-weight: 600;
  transition: var(--transition);
  backdrop-filter: blur(10px);
}

.login-submit-button:hover:not(:disabled) {
  background: rgba(255, 255, 255, 0.3);
  transform: translateY(-2px);
  box-shadow: var(--shadow);
}

.login-submit-button:disabled {
  cursor: not-allowed;
  opacity: 0.7;
}

/* ========================================
   FORM VALIDATION & ERROR STATES
   ======================================== */

.register-error {
  color: #dc3545;
  background-color: rgba(220, 53, 69, 0.1);
  padding: 10px;
  border-radius: 4px;
  margin-top: 10px;
  font-size: 14px;
  border: 1px solid rgba(220, 53, 69, 0.2);
}

.login-error {
  color: #dc3545;
  background-color: rgba(220, 53, 69, 0.1);
  padding: 10px;
  border-radius: 4px;
  margin-top: 10px;
  font-size: 14px;
  border: 1px solid rgba(220, 53, 69, 0.3);
}

/* ========================================
   RATE INFO FORMS
   ======================================== */

.rate-info {
  padding: 15px;
  background: #808080;
  border-radius: var(--border-radius);
  border: 1px solid rgba(255, 255, 255, 0.2);
}

.rate-title {
  font-size: 14px;
  font-weight: 600;
  color: var(--text-light);
  margin-bottom: 8px;
}

.rate-details {
  font-size: 12px;
  opacity: 0.9;
  line-height: 1.4;
}

/* ========================================
   RESPONSIVE FORM DESIGN
   ======================================== */

@media (max-width: 768px) {
  .form-grid-2 {
    grid-template-columns: 1fr;
  }
}

/* ========================================
   TEST DATA BUTTONS
   ======================================== */

.test-data-btn {
  padding: 8px 16px;
  background-color: #17a2b8;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  font-size: 14px;
  margin-bottom: 15px;
  transition: var(--transition);
}

.test-data-btn:hover {
  background-color: #138496;
  transform: translateY(-1px);
}

.login-test-button {
  padding: 6px 12px;
  background-color: #28a745;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  font-size: 13px;
  margin-bottom: 10px;
  transition: var(--transition);
}

.login-test-button:hover {
  background-color: #218838;
}

src/styles/layout.css.content:
/* ========================================
   LAYOUT STYLES
   Grid systems, containers, page layout
   ======================================== */

/* ========================================
   LAYOUT COMPONENTS
   ======================================== */

.page-container {
  min-height: 100vh;
  background: var(--background-gradient);
  padding: 20px;
}

.content-wrapper {
  max-width: 1200px;
  margin: 0 auto;
}

.card {
  background: #808080;
  border-radius: var(--border-radius-lg);
  padding: 30px;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  box-shadow: var(--shadow-lg);
}

.card-header {
  text-align: center;
  margin-bottom: 30px;
}

.card-title {
  font-size: 28px;
  font-weight: 700;
  margin-bottom: 8px;
  color: var(--text-light);
}

.card-subtitle {
  font-size: 16px;
  opacity: 0.8;
  color: var(--text-light);
}

/* ========================================
   GRID LAYOUTS
   ======================================== */

.grid-2 {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 30px;
}

.grid-3 {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 20px;
}

.grid-responsive {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 20px;
}

/* ========================================
   SPECIFIC LAYOUT SECTIONS
   ======================================== */

/* Hero Section */
.hero-section {
  text-align: center;
  margin-bottom: 40px;
}

.hero-title {
  font-size: 48px;
  font-weight: 800;
  margin-bottom: 16px;
  background: linear-gradient(45deg, #FFD700, #FFA500);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.hero-subtitle {
  font-size: 20px;
  opacity: 0.9;
  margin-bottom: 32px;
}

.hero-stats {
  display: flex;
  justify-content: center;
  gap: 40px;
  margin-bottom: 40px;
}

.stat-item {
  text-align: center;
}

.stat-number {
  font-size: 32px;
  font-weight: 700;
  color: #FFD700;
  display: block;
}

.stat-label {
  font-size: 14px;
  opacity: 0.8;
  margin-top: 4px;
}

/* Calculator Sections */
.calculator-section {
  background: #808080;
  border-radius: var(--border-radius-lg);
  padding: 30px;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
}

.calculator-header {
  font-size: 24px;
  font-weight: 700;
  text-align: center;
  margin-bottom: 30px;
  color: var(--text-light);
}

/* Results Display */
.results-container {
  background: #808080;
  border-radius: var(--border-radius-lg);
  padding: 30px;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
}

.results-header {
  font-size: 24px;
  font-weight: 700;
  text-align: center;
  margin-bottom: 30px;
  color: var(--text-light);
}

.results-grid {
  display: grid;
  gap: 15px;
}

.result-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 15px;
  background: #808080;
  border-radius: var(--border-radius);
}

.result-label {
  font-size: 14px;
  opacity: 0.8;
}

.result-value {
  font-weight: 600;
}

.result-primary {
  background: rgba(255, 255, 255, 0.15);
  border: 1px solid rgba(255, 255, 255, 0.3);
  padding: 20px;
}

.result-primary .result-label {
  font-size: 16px;
  font-weight: 600;
}

.result-primary .result-value {
  font-size: 18px;
  font-weight: 700;
  color: #FFD700;
}

.result-highlight {
  color: #98FB98;
}

.result-accent {
  color: #FFB6C1;
}

/* ========================================
   EMPTY STATES
   ======================================== */

.empty-state {
  text-align: center;
  opacity: 0.7;
  font-size: 14px;
  margin-top: 60px;
}

.empty-icon {
  font-size: 48px;
  margin-bottom: 16px;
}

.empty-title {
  font-size: 18px;
  font-weight: 600;
  margin-bottom: 8px;
}

.empty-description {
  line-height: 1.5;
}

.empty-meta {
  margin-top: 20px;
  font-size: 12px;
  line-height: 1.5;
}

.empty-meta strong {
  color: #FFD700;
}

/* ========================================
   RESPONSIVE DESIGN
   ======================================== */

@media (max-width: 768px) {
  .grid-2 {
    grid-template-columns: 1fr;
  }

  .grid-3 {
    grid-template-columns: 1fr;
  }

  .hero-title {
    font-size: 32px;
  }

  .hero-stats {
    flex-direction: column;
    gap: 20px;
  }

  .page-container {
    padding: 10px;
  }

  .card {
    padding: 20px;
  }
}

@media (max-width: 480px) {
  .hero-title {
    font-size: 24px;
  }

  .card-title {
    font-size: 24px;
  }

  .calculator-header,
  .results-header {
    font-size: 20px;
  }
}

/* ========================================
   UTILITIES
   ======================================== */

.text-center { text-align: center; }
.text-left { text-align: left; }
.text-right { text-align: right; }

.mb-10 { margin-bottom: 10px; }
.mb-20 { margin-bottom: 20px; }
.mb-30 { margin-bottom: 30px; }

.mt-10 { margin-top: 10px; }
.mt-20 { margin-top: 20px; }
.mt-30 { margin-top: 30px; }

.opacity-50 { opacity: 0.5; }
.opacity-70 { opacity: 0.7; }
.opacity-80 { opacity: 0.8; }
.opacity-90 { opacity: 0.9; }

.font-bold { font-weight: 700; }
.font-semibold { font-weight: 600; }

.color-gold { color: #FFD700; }
.color-green { color: #98FB98; }
.color-pink { color: #FFB6C1; }

