/* ========================================
   PAGE-SPECIFIC STYLES
   Home, login, register, account, credit application pages
   ======================================== */

/* ========================================
   HOME PAGE SPECIFIC
   ======================================== */

.home-container {
  min-height: 100vh;
}

.home-content {
  padding: 20px;
  text-align: center;
}

.home-content-wrapper {
  max-width: 800px;
  margin: 0 auto;
}

.welcome-text {
  font-size: 18px;
  line-height: 1.6;
  margin-bottom: 30px;
}

/* ========================================
