}

.calculator-results-title {
  font-size: 20px;
  font-weight: 600;
  margin-bottom: 20px;
  color: #ffffff;
  text-align: center;
}

.deposit-results-grid {
  display: grid;
  gap: 15px;
}

.deposit-result-item {
