  border-radius: 8px;
}

.deposit-result-item-total {
  display: flex;
  justify-content: space-between;
  padding: 15px;
  background: rgba(255, 255, 255, 0.2);
  border-radius: 10px;
  border: 2px solid rgba(255, 255, 255, 0.3);
}

.deposit-result-label {
  font-size: 14px;
  opacity: 0.8;
}

.deposit-result-value {
  font-weight: 600;
}

.deposit-result-value-green {
  font-weight: 600;
