}

.loan-rate-details {
  font-size: 12px;
  opacity: 0.9;
  line-height: 1.4;
}

.loan-results-grid {
  display: grid;
  gap: 15px;
}

.loan-result-item {
  display: flex;
  justify-content: space-between;
  padding: 12px;
  background: #808080;
  border-radius: 8px;
}

.loan-result-item-enhanced {
  display: flex;
  justify-content: space-between;
  padding: 12px;
  background: rgba(255, 255, 255, 0.15);
  border-radius: 8px;
}

.loan-result-item-total {
