  font-size: 24px;
}

.account-details-grid {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 20px;
  margin-bottom: 25px;
}

.account-details-secondary-grid {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 20px;
  margin-bottom: 20px;
}

.account-number-box {
  padding: 20px;
  background-color: rgba(0, 123, 255, 0.1);
  border-radius: 8px;
  text-align: center;
}

.account-balance-box {
  padding: 20px;
  background-color: rgba(40, 167, 69, 0.1);
  border-radius: 8px;
  text-align: center;
}

.account-type-box {
  padding: 15px;
  background-color: rgba(108, 117, 125, 0.1);
  border-radius: 8px;
  text-align: center;
}

.account-status-box {
  padding: 15px;
  background-color: rgba(23, 162, 184, 0.1);
  border-radius: 8px;
  text-align: center;
}

.account-field-label {
  font-size: 14px;
  opacity: 0.8;
  margin-bottom: 8px;
}

.account-field-label-small {
