  color: #28a745;
}

.account-info-value {
  font-size: 16px;
  font-weight: bold;
}

.account-status-value {
  font-size: 16px;
  font-weight: bold;
  color: #28a745;
}

.account-date-info {
  text-align: center;
  font-size: 14px;
  opacity: 0.7;
  padding: 10px;
}

.account-services-card {
  padding: 20px;
  border-radius: 8px;
  text-align: center;
}

.account-services-title {
  margin-top: 0;
  margin-bottom: 15px;
  color: #007bff;
}

.account-services-description {
  opacity: 0.8;
  margin-bottom: 15px;
}

.account-development-notice {
  padding: 15px;
  background-color: rgba(255, 193, 7, 0.1);
  border-radius: 8px;
  font-style: italic;
}

.account-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 20px;
  margin-bottom: 30px;
}

.account-card {
  background: #808080;
  border-radius: var(--border-radius);
  padding: 20px;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
}

.account-label {
  font-size: 14px;
  opacity: 0.8;
  margin-bottom: 8px;
}

.account-value {
  font-size: 18px;
  font-weight: 600;
  color: var(--text-light);
}

.account-highlight {
  color: #FFD700;
  font-size: 24px;
  font-weight: 700;
}

/* ========================================
   REGISTER PAGE SPECIFIC
   ======================================== */

.register-container {
  min-height: 100vh;
  padding: 20px;
}

.register-content {
  max-width: 600px;
  margin: 0 auto;
}

.register-header {
  text-align: center;
  margin-bottom: 30px;
}

.register-title {
  font-size: 28px;
  font-weight: 700;
  margin-bottom: 8px;
  color: var(--text-light);
}

.register-subtitle {
  font-size: 20px;
  font-weight: 600;
  margin-bottom: 20px;
  color: var(--text-light);
}

/* ========================================
   LOGIN PAGE
   ======================================== */

.login-page {
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
